import './Control.css';
import React, { useRef, useState, useEffect } from 'react';
import RecordAudio from '../../RecordAudio/RecordAudio'
import ConfirmationModal from '../../ConfirmationModal/ConfirmationModal'
import { NotificationManager } from 'react-notifications';
import { postAudioMsg, postMsg } from '../../Api/Api'

const msgsCode = {"StartVideo":2, "StopVideo": 3, "Attack": 4, "Defense": 5, "Escape": 6, "ReGroup": 7}
const msgs = {2: "Stream start request", 3: "Stream end request" , 4: "Attack", 5: "Defense", 6: "Escape", 7: "Regroup" }

function Control({ unit, port, type }) {
    const recordAudioRef = useRef(null);
    const confirmationModalRef = useRef(null);
    const [confirmationMsgCode, setConfirmationMsgCode] = useState(null)

    var onMsgIconClick = (msgCode) => {
        setConfirmationMsgCode(msgCode)
        confirmationModalRef.current.openModal();
    }

    var onSendMsg = () => {
        NotificationManager.info(msgs[confirmationMsgCode] + " message will be sent to " + unit.name)
        postMsg(unit.ip, {code: confirmationMsgCode,}, port)
    }

    var onSendAudio = (audio) => {
        NotificationManager.info("Your audio message will be sent to " + unit.name)
        postAudioMsg(unit.ip, audio, port)
    }
 
    return (
        <div className="control-container">
            {unit ? 
            <>
            <ConfirmationModal onSend={onSendMsg} msgCode={confirmationMsgCode} name={unit.name} ref={confirmationModalRef} > </ConfirmationModal>  
            <RecordAudio onSend={onSendAudio} ref={recordAudioRef}></RecordAudio>
            <div data-augmented-ui="tr-clip br-clip bl-clip-y border" class="control-item"></div>
            { type === "group" ?
            <>
                <img alt="defense" onClick={() => onMsgIconClick(msgsCode["Defense"])} className="defense-icon" src="data:image/svg+xml;base64," />
                <img alt="escape" onClick={() => onMsgIconClick(msgsCode["Escape"])} className="escape-icon" src="data:image/svg+xml;base64," />
                <img alt="regroup" onClick={() => onMsgIconClick(msgsCode["ReGroup"])} className="regroup-icon" src="data:image/svg+xml;base64," />
                <img alt="group-attack" onClick={() => onMsgIconClick(msgsCode["Attack"])} className="group-attack-icon" src="data:image/svg+xml;base64," />
            </>
                : 
                <>
                <img alt="start-video" onClick={() => onMsgIconClick(msgsCode["StartVideo"])} className="video-icon" src="data:image/svg+xml;base64," />
                <img alt="stop-video" onClick={() => onMsgIconClick(msgsCode["StopVideo"])} className="stop-icon" src="data:image/svg+xml;base64," />
                <img alt="record-audio" onClick = {() => {recordAudioRef.current.openModal()}} className="audio-icon" src="data:image/svg+xml;base64," />            
                <img alt="unit-attack" onClick={() => onMsgIconClick(msgsCode["Attack"])} className="unit-attack-icon" src="data:image/svg+xml;base64," />
                </>
            }
            <svg className="ctrl-svg" version="1.1" id="Layer_1" xmlns="http://www.w3.org/2000/svg" x="0px" y="0px"
                viewBox="0 0 1000 1000" styles="enable-background:new 0 0 1000 1000;" space="preserve">
                <circle class="st0" cx="500" cy="500" r="302.8">
                    <animateTransform attributeType="xml"
                        attributeName="transform"
                        type="rotate"
                        from="0 500 500"
                        to="360 500 500"
                        dur="100s"
                        repeatCount="indefinite" />
                </circle>
                <circle class="st1" cx="500" cy="500" r="237.7">
                    <animateTransform attributeType="xml"
                        attributeName="transform"
                        type="rotate"
                        from="0 500 500"
                        to="360 500 500"
                        dur="40s"
                        repeatCount="indefinite" />
                </circle>
                <circle class="st2" cx="500" cy="500" r="366.8" transform="rotate(0 500 500)">
                    <animateTransform attributeType="xml"
                        attributeName="transform"
                        type="rotate"
                        from="0 500 500"
                        to="-360 500 500"
                        dur="50s"
                        repeatCount="indefinite" />
                </circle>
                <circle class="st3" cx="500" cy="500" r="385.1" />
            </svg> 
            </> :
             <div className="no-data-gallery-msg"> 
                <p> No selected unit </p> 
            </div>
            }
        </div>
    );
}
export default Control;
